import string

def make_acronym(phrase: str) -> str:
    if not isinstance(phrase, str):
        return 'Not a string'
    if phrase == '':
        return ''
    phrase = phrase.strip()
    if any((ch for ch in phrase if ch not in string.ascii_letters + ' ')):
        return 'Not letters'
    words = phrase.split()
    acronym = ''.join((word[0].upper() for word in words))
    return acronym

