from sympy import symbols, Eq, solve

def solve_for_x(equation: str) -> int:
    x = symbols('x')
    eq = Eq(*map(lambda side: eval(side.strip(), {'x': x}), equation.split('=')))
    solution = solve(eq, x)
    return int(solution[0])

