import re

def solve_for_x(equation: str) -> int:
    equation = equation.replace(' ', '')
    left_side, right_side = equation.split('=')
    if 'x' in left_side:
        terms = re.split('(\\+|-)', left_side)
        terms = [term for term in terms if term != '']
        constant_part_left = 0
        for i in range(0, len(terms), 2):
            if 'x' not in terms[i]:
                constant_part_left += int(terms[i]) if i == 0 or terms[i - 1] == '+' else -int(terms[i])
        right_side_value = eval(right_side) - constant_part_left
        x_index = terms.index('x')
        if x_index > 0 and terms[x_index - 1] in ('+', '-'):
            coefficient = 1 if terms[x_index - 1] == '+' else -1
        else:
            coefficient = 1
        return right_side_value // coefficient
    elif 'x' in right_side:
        terms = re.split('(\\+|-)', right_side)
        terms = [term for term in terms if term != '']
        constant_part_right = 0
        for i in range(0, len(terms), 2):
            if 'x' not in terms[i]:
                constant_part_right += int(terms[i]) if i == 0 or terms[i - 1] == '+' else -int(terms[i])
        left_side_value = eval(left_side) - constant_part_right
        x_index = terms.index('x')
        if x_index > 0 and terms[x_index - 1] in ('+', '-'):
            coefficient = 1 if terms[x_index - 1] == '+' else -1
        else:
            coefficient = 1
        return left_side_value // coefficient

