def trump_detector(ts: str) -> float:
    import re
    from math import floor
    ts_lower = ts.lower()
    vowels = 'aeiou'
    total_vowels = 0
    extra_vowels = 0
    for vowel in vowels:
        total_vowels += ts_lower.count(vowel)
    matches = re.findall('(.)\\1+', ts_lower)
    for match in matches:
        if match[0] in vowels:
            extra_vowels += len(match) - 1
    if total_vowels == 0:
        return 0.0
    else:
        trump_index = extra_vowels / total_vowels
    return round(trump_index, 2)

def trump_detector(ts: str) -> float:
    import re
    from math import floor
    ts = ts.lower()
    vowels = 'aeiou'
    total_vowels = 0
    repeated_vowels = 0
    pattern = '(.)\\1*'
    for char in ts:
        if char in vowels:
            total_vowels += 1
    matches = re.findall(pattern, ts)
    for match in matches:
        if len(match) > 1 and match[0] in vowels:
            repeated_vowels += len(match) - 1
    if total_vowels == 0:
        return 0.0
    trump_index = round(repeated_vowels / total_vowels, 2)
    return trump_index

