def trump_detector(ts: str) -> float:
    ts = re.sub('[^a-z]', '', ts.lower())
    vowel_counts = {'a': 0, 'e': 0, 'i': 0, 'o': 0, 'u': 0}
    repeated_vowels_count = 0
    total_vowel_count = 0
    previous_char = ''
    for char in ts:
        if char in vowel_counts:
            total_vowel_count += 1
            if char == previous_char:
                repeated_vowels_count += 1
            vowel_counts[char] += 1
        previous_char = char
    if total_vowel_count == 0:
        return 0.0
    trump_index = round(repeated_vowels_count / total_vowel_count, 2)
    return trump_index

def trump_detector(ts: str) -> float:
    from collections import Counter
    import re
    clean_ts = re.sub('[^a-z]', '', ts.lower())
    vowels = 'aeiou'
    vowel_str = ''.join([char for char in clean_ts if char in vowels])
    repeated_vowels = 0
    prev_char = None
    for char in vowel_str:
        if char == prev_char:
            repeated_vowels += 1
            prev_char = None
        else:
            prev_char = char
    total_vowels = len(vowel_str)
    if total_vowels <= 0:
        return round(0.0, 2)
    trumpiness_index = repeated_vowels / (total_vowels - repeated_vowels)
    return round(trumpiness_index, 2)

