from math import ceil

def trump_detector(ts: str) -> float:
    ts = re.sub('[^a-z]', '', ts.lower())
    vowel_counts = {'a': 0, 'e': 0, 'i': 0, 'o': 0, 'u': 0}
    repeated_vowels = 0
    total_vowels = 0
    i = 0
    while i < len(ts):
        if ts[i] in vowel_counts:
            total_vowels += 1
            count = 1
            while i + 1 < len(ts) and ts[i] == ts[i + 1]:
                count += 1
                i += 1
            if count > 1:
                repeated_vowels += count - 1
        i += 1
    if total_vowels == 0:
        return 0.0
    trump_index = round(repeated_vowels / total_vowels * 100) / 100
    return trump_index

