        power = pow(10, n, p)
        return (power - 1) % p == 0 or (power + 1) % p == 0

    def is_n_altsum_prime(n, p):
        power = pow(10, n, p)
        return (power + 1) % p == 0

    def find_smallest_n(p):
        n = 1
        while True:
            if is_n_sum_prime(n, p):
                return f'{n}-sum'
            if is_n_altsum_prime(n, p):
