            n += 1
    if p == 2 or p == 5:
        return '1-sum'
    return find_smallest_n(p)

from typing import Tuple

def get_sum_or_altsum(p: int) -> Tuple[int, str]:
    """
    Determines whether the prime number p is an n-sum or n-altsum prime,
    and returns the smallest positive integer n for its divisibility test.
    """
    if p == 2 or p == 5:
        return (1, 'sum')
    for n in range(1, 100):
        if pow(10, n, p) == 1:
            return (n, 'sum')
        if pow(10, n, p + 1) == 1:
            return (n, 'altsum')
    return (None, '')

def solve(p: int) -> str:
    n, kind = get_sum_or_altsum(p)
    if n is None:
        return f'No known {p}-sum or {p}-altsum pattern found.'
    else:
        return f'{p} is a {n}-{kind} prime.'

