def solve(p: int) -> str:
    if p == 2 or p == 5:
        return '1-sum'

    def is_n_sum_prime(n, p):
        """Check if p is an n-sum prime."""
        return pow(10, n, p) == 1

    def is_n_altsum_prime(n, p):
        """Check if p is an n-altsum prime."""
        return pow(10, n, p) == p - 1
    for n in range(1, 100):
        if is_n_sum_prime(n, p):
            return f'{n}-sum'
        if is_n_altsum_prime(n, p):
            return f'{n}-altsum'
    return 'No specific sum or altsum found'

from sympy import isprime

def find_smallest_n(p: int) -> str:
    if not isprime(p):
        return 'Input must be a prime number'
    for n in range(1, p + 1):
        if check_sum_rule(p, n) or check_altsum_rule(p, n):
            return f'{n}-sum' if check_sum_rule(p, n) else f'{n}-altsum'
    return 'Not found'

def check_sum_rule(p: int, n: int) -> bool:
    return pow(10, n, p) == 1

