from typing import Tuple

def solve(p: int) -> str:
    """
    Finds the smallest positive integer `n` such that `p`'s divisibility testing is `n-sum` or `n-altsum`.

    Args:
    p (int): A prime number.

    Returns:
    str: The type of divisibility rule for the given prime number.
    """

    def sum_based_divisibility_test(p: int) -> Tuple[bool, int]:
        n = 1
        while True:
            if all((int(f'{i:0{n}d}') % p == 0 for i in range(0, 10 ** n, p))):
                return (True, n)
            n += 1

    def altsum_based_divisibility_test(p: int) -> Tuple[bool, int]:
        n = 1
        while True:
            valid = True
            for base in range(p):
                num_str = ''
                for exp in range(n):
                    num_str = f'{base % 10}{num_str}'
                    base //= 10
                num_sum = sum([(-1) ** i * int(num_str[i:i + n]) for i in range(0, len(num_str), n)])
                if num_sum % p != 0:
                    valid = False
                    break
            if valid:
                return (True, n)
            n += 1
    known_sum_primes = {3: 1, 37: 3, 41: 5, 7: 3, 11: 1, 47: 23, 73: 4}
    if p in known_sum_primes:
        return f"{p} is a '{known_sum_primes[p]}-sum' prime"
    is_sum, n_sum = sum_based_divisibility_test(p)
    if is_sum:
        return f"{p} is a '{n_sum}-sum' prime"
    is_altsum, n_altsum = altsum_based_divisibility_test(p)
    if is_altsum:
        return f"{p} is a '{n_altsum}-altsum' prime"
    raise Exception('No divisibility rule found for this prime')

