            return (group_size, 'sum')
    return (-1, 'unknown')

def check_sum_group(p: int, group_size: int) -> bool:
    for num in range(1, 10 ** 6):
        if p == 3 and group_size == 1:
            if sum((int(d) for d in str(num))) % p == 0:
                return True
        elif p == 37 and group_size == 3:
            groups = [str(num)[i:i + group_size] for i in range(0, len(str(num)), group_size)]
            if sum((int(g) for g in groups)) % p == 0:
                return True
        elif p == 41 and group_size == 5:
            groups = [str(num)[i:i + group_size] for i in range(0, len(str(num)), group_size)]
            if sum((int(g) for g in groups)) % p == 0:
                return True
        elif p == 7 and group_size == 7:
            groups = [str(num)[i:i + group_size] for i in range(0, len(str(num)), group_size)]
            if sum((int(g) for g in groups)) % p == 0:
                return True
        elif p == 99 and group_size == 99:
            groups = [str(num)[i:i + group_size] for i in range(0, len(str(num)), group_size)]
            if sum((int(g) for g in groups)) % p == 0:
                return True
    return False

from itertools import chain

def solve(p: int) -> str:

    def is_n_sum_prime(n, p):
        """Check if p can be tested for divisibility by summing groups of n digits."""
        if n == 1:
            return all((sum((int(digit) for digit in str(q))) % p == 0 for q in range(0, p * 2, p)))
        else:
            for k in range(1, 10 ** n):
                s = sum((int(str(k).zfill(n)[i:i + n]) for i in range(0, len(str(k)), n)))
                if s % p != 0 and (k + p - k % p) % p == 0:
                    return False
            return True

    def is_n_altsum_prime(n, p):
        """Check if p can be tested for divisibility by alternately summing/subtracting groups of n digits."""
        for k in range(1, 10 ** n):
            alt_sum = sum(((-1) ** i * int(str(k).zfill(n)[i:i + n]) for i in range(0, len(str(k)), n)))
            if alt_sum % p != 0 and (k + p - k % p) % p == 0:
                return False
        return True
    if p == 2 or p == 5:
        return '1-sum'
    for n in chain([1], range(2, 100)):
        if is_n_sum_prime(n, p):
            return f'{n}-sum'
    for n in chain([1], range(2, 100)):
        if is_n_altsum_prime(n, p):
            return f'{n}-altsum'
    raise ValueError('No suitable n found for given prime number.')

