import math
from typing import Tuple

def power_law(p1: Tuple[float, float], p2: Tuple[float, float], x3: float) -> int:
    x1, y1 = p1
    x2, y2 = p2
    if x1 == x2:
        raise ValueError('x1 and x2 cannot be equal')
    alpha = math.log(y2 / y1) / math.log(x2 / x1)
    y3 = y1 * (x3 / x1) ** alpha
    return round(y3)

