def human_years_cat_years_dog_years(x: int) -> List[int]:
    human_years = x
    cat_years = 0
    dog_years = 0
    if x >= 1:
        cat_years += 15
    if x >= 2:
        cat_years += 9
        for year in range(2, x):
            cat_years += 4
    if x >= 1:
        dog_years += 15
    if x >= 2:
        dog_years += 9
        for year in range(2, x):
            dog_years += 5
    return [human_years, cat_years, dog_years]

