        excess_poor = len(poorest_indices) - len(richest_indices)
        if excess_poor > 0:
            for i in range(excess_poor):
                if richest_indices:
                    population[poorest_indices[i]] += 1
                    population[richest_indices[i % len(richest_indices)]] -= 1
