            elif value == minimum and remaining_surplus > 0:
                for j in reversed(range(len(sorted_population))):
                    ridx, rval = sorted_population[j]
                    if rval > minimum:
                        population_after_adjustment[idx] += 1
                        population_after_adjustment[ridx] -= 1
                        remaining_surplus -= 1
                        break
                if remaining_surplus == 0:
                    break
    return population_after_adjustment

