def round_to_five(numbers: List[float]) -> List[float]:
    """
    Given an array of numbers, return an array, with each member of input array rounded to a nearest number, divisible by 5.
    Roundings have to be done like "in real life": `22.5 -> 25`
    """

    def round_individual(num: float) -> float:
        return round(num / 5) * 5

    def real_life_round(num: float) -> float:
        remainder = num % 5
        if remainder >= 2.5:
            return num + (5 - remainder) if num > 0 else num - (5 + remainder)
        else:
            return num - remainder if num > 0 else num + (5 - remainder)
        return round_individual(num)
    return [real_life_round(num) for num in numbers]

