def charcheck(text: str, mx: int, spaces: bool) -> List[Union[bool, str]]:
    if not spaces:
        text_no_spaces = text.replace(' ', '')
        truncated_text = text_no_spaces[:mx]
    else:
        truncated_text = text[:mx]
    if spaces and len(text) <= mx or (not spaces and len(text_no_spaces) <= mx):
        return [True, text]
    else:
        if not spaces:
            index = 0
            final_text = list(truncated_text)
            for i in range(len(text)):
                if index >= len(truncated_text):
                    break
                if text[i] != ' ':
                    index += 1
                else:
                    final_text.insert(index, ' ')
                    index += 1
            truncated_text = ''.join(final_text)
        return [False, truncated_text]

