def convert_recipe(recipe: str) -> str:
    unit_to_grams = {'tbsp': 15, 'tsp': 5}
    words = recipe.split()
    converted_recipe = []
    for i, word in enumerate(words):
        if word in unit_to_grams:
            prev_word = words[i - 1]
            try:
                value = float(prev_word)
                grams = ceil(value * unit_to_grams[word])
                converted_recipe.append(f'{word} ({grams}g)')
            except ValueError:
                converted_recipe.append(word)
        else:
            converted_recipe.append(word)
    return ' '.join(converted_recipe)

