def convert_recipe(recipe: str) -> str:
    """Converts measurements in a recipe from tablespoons (tbsp) and teaspoons (tsp) to grams.

    Args:
        recipe (str): A string containing the recipe with measurements in tbsp and tsp.

    Returns:
        str: The recipe string with measurements converted to grams.
    """
    words = recipe.split()
    for i, word in enumerate(words):
        if word.endswith(('tbsp', 'tsp')):
            try:
                value = float(words[i - 1])
                if 'tbsp' in word:
                    grams = ceil(value * 15)
                elif 'tsp' in word:
                    grams = ceil(value * 5)
                words[i] = f'{word} ({grams}g)'
            except ValueError:
                continue
    return ' '.join(words)

