                grams = ceil(float(amount) * 15)
                words[i] += f' ({grams}g)'
            except ValueError:
                pass
        elif word.endswith('tsp'):
            amount = word[:-3]
            try:
                grams = ceil(float(amount) * 5)
                words[i] += f' ({grams}g)'
            except ValueError:
                pass
    return ' '.join(words)

