def convert_recipe(recipe: str) -> str:
    """
    Converts measurements in the recipe from tablespoons and teaspoons to grams,
    appending the largest equivalent integer (rounding up).

    Args:
        recipe (str): The original recipe string containing 'tbsp' and 'tsp'.

    Returns:
        str: The updated recipe with measurements in grams.
    """
    words = recipe.split()
    converted_words = []
    for word in words:
        if 'tbsp' in word:
            try:
                num = float(word.replace('tbsp', ''))
                grams = ceil(num * 15)
                converted_words.append(f'{word} ({grams}g)')
            except ValueError:
                converted_words.append(word)
        elif 'tsp' in word:
            try:
                num = float(word.replace('tsp', ''))
                grams = ceil(num * 5)
                converted_words.append(f'{word} ({grams}g)')
            except ValueError:
                converted_words.append(word)
        else:
            converted_words.append(word)
    return ' '.join(converted_words)

