def convert_recipe(recipe: str) -> str:
    """
    Converts all the measures in the recipe from tablespoon (`tbsp`) and teaspoon (`tsp`) to grams.
    Considering `1 tbsp = 15g` and `1 tsp = 5g`, append to the end of the measurement the biggest equivalent integer (rounding up).
    """
    words = recipe.split()
    for i, word in enumerate(words):
        if word.endswith('tbsp'):
            amount = word[:-len('tbsp')].strip()
            if amount.replace('.', '', 1).isdigit():
                grams = ceil(float(amount) * 15)
                words[i] = f'{word} ({grams}g)'
        elif word.endswith('tsp'):
            amount = word[:-len('tsp')].strip()
            if amount.replace('.', '', 1).isdigit():
                grams = ceil(float(amount) * 5)
                words[i] = f'{word} ({grams}g)'
    return ' '.join(words)

