def convert_recipe(recipe: str) -> str:
    """
    Converts measurements in the recipe from tablespoons (tbsp) and teaspoons (tsp) to grams.
    Uses the conversion rates: 1 tbsp = 15g and 1 tsp = 5g, rounding up to the nearest gram.

    Parameters:
    - recipe (str): The recipe as a string containing measurements in tbsp and tsp.

    Returns:
    - str: The updated recipe with measurements converted to grams.
    """

    def convert_measurement(value: str, unit: str) -> str:
        try:
            num = float(value)
            if unit == 'tbsp':
                grams = ceil(num * 15)
            elif unit == 'tsp':
                grams = ceil(num * 5)
            else:
                return f'{value}{unit}'
            return f'{value}{unit} ({grams}g)'
        except ValueError:
            return f'{value}{unit}'
    words = recipe.split()
    converted_words = []
    i = 0
    while i < len(words):
        word = words[i]
        if word.endswith(('tbsp', 'tsp')):
            value = word[:-4]
            unit = word[-4:]
            converted_word = convert_measurement(value, unit)
            converted_words.append(converted_word)
        else:
            converted_words.append(word)
        i += 1
    return ' '.join(converted_words)

