from math import ceil

def convert_recipe(recipe: str) -> str:
    """
    Converts all measurements in the given recipe from tablespoons (tbsp) and teaspoons (tsp) to grams.
    1 tbsp = 15g and 1 tsp = 5g. Appends the biggest equivalent integer (rounding up) to the end of the measurement.

    :param recipe: The original recipe string with measurements.
    :return: The updated recipe string with measurements converted to grams.
    """

    def to_grams(value, unit):
        if unit == 'tbsp':
            return ceil(value * 15)
        elif unit == 'tsp':
            return ceil(value * 5)
        else:
            return None
    words = recipe.split()
    result = []
    i = 0
    while i < len(words):
        word = words[i]
        if word.endswith(('tbsp', 'tsp')):
            try:
                value = float(words[i - 1])
                unit = word
                grams = to_grams(value, unit)
                result.append(f'{value} {unit} ({grams}g)')
                i += 1
            except ValueError:
                result.append(word)
        else:
            result.append(word)
        i += 1
    return ' '.join(result)
