def string_parse(string: str) -> str:
    if not isinstance(string, str):
        return 'Please enter a valid string'
    result = []
    i = 0
    while i < len(string):
        count = 1
        while i + 1 < len(string) and string[i] == string[i + 1]:
            count += 1
            i += 1
        if count > 2:
            result.append(f'{string[i]}[{string[i] * (count - 2)}]')
        else:
            result.append(string[i] * count)
        i += 1
    return ''.join(result)

