def string_parse(string: str) -> str:
    if not isinstance(string, str):
        return 'Please enter a valid string'
    result = []
    temp_char = ''
    count = 0
    for char in string:
        if char.isalpha():
            if char == temp_char:
                count += 1
            else:
                temp_char = char
                count = 1
            if count <= 2:
                result.append(char)
            else:
                result.append(']' + char)
        else:
            result.append(char)
            temp_char = ''
            count = 0
    result_str = ''.join(result)
    corrected_result = []
    bracket_opened = False
    for i in range(len(result_str)):
        if i > 0 and result_str[i] == result_str[i - 1] and result_str[i - 1].isalpha() and (result_str[i] != ']'):
            if not bracket_opened:
                corrected_result.append('[')
                bracket_opened = True
            corrected_result.append(result_str[i])
        else:
            if bracket_opened:
                corrected_result.append(']')
                bracket_opened = False
            corrected_result.append(result_str[i])
    if bracket_opened:
        corrected_result.append(']')
    return ''.join(corrected_result).replace(']', '', 1)

from typing import List

def lostsheep(friday: List[int], saturday: List[int], total: int) -> int:
    returned_sheep = sum(friday) + sum(saturday)
    lost_sheep = total - returned_sheep
    return max(0, lost_sheep)

