    good_fairy_mapping = {'r': 1, 'R': 2, 'c': 1, 'C': 2}
    evil_fairy_mapping = {'p': 1, 'P': 2, 's': 1, 'S': 2}
    counter = collections.defaultdict(int)
    if fairy == 'good':
        for char in sentence:
