def missing_angle(h: float, a: float, o: float) -> int:
    """
    Calculate the missing angle  in degrees to the nearest integer.

    Parameters:
    h (float): The hypotenuse of the triangle.
    a (float): The adjacent side of the triangle.
    o (float): The opposite side of the triangle.

    Returns:
    int: The angle  in degrees.
    """
    if [h, a, o].count(0) != 1:
        raise ValueError('Exactly one of the arguments must be zero.')
    if h == 0:
        h = math.sqrt(a ** 2 + o ** 2)
        theta = math.degrees(math.asin(o / h))
    elif a == 0:
        theta = math.degrees(math.acos(a / h))
    elif o == 0:
        theta = math.degrees(math.atan(o / a))
    else:
        raise ValueError('One of the sides must be zero.')
    return round(theta)

