from typing import List

def diff(arr: List[str]) -> str:
    if not arr:
        return False
    parsed_couples = []
    for couple in arr:
        nums = couple.split(',')
        if len(nums) != 2 or not all((num.strip().isdigit() for num in nums)):
            return False
        parsed_couples.append((int(nums[0].strip()), int(nums[1].strip())))
    max_diff = -1
    max_index = -1
    for index, (a, b) in enumerate(parsed_couples):
        current_diff = abs(a - b)
        if current_diff > max_diff:
            max_diff = current_diff
            max_index = index
    if max_diff == 0:
        return False
    return arr[max_index]
