def is_anagram(test, original):
    """Check if two strings are anagrams, ignoring case."""
    test = ''.join(filter(str.isalpha, test.lower()))
    original = ''.join(filter(str.isalpha, original.lower()))
    return Counter(test) == Counter(original)

def make_acronym(phrase):
    if not isinstance(phrase, str):
        return 'Not a string'
    if phrase == '':
        return ''
    if not all((c.isalpha() or c.isspace() for c in phrase)):
        return 'Not letters'
    words = phrase.split()
    acronym = ''.join((word[0].upper() for word in words))
    return acronym

