from sympy import symbols, Eq, solve

def solve_for_x(equation):
    """
    Solve for X in the given equation and return x's value.

    Args:
    equation (str): The equation to solve for X. Only whole numbers are used.

    Returns:
    int: The value of X.
    """
    x = symbols('x')
    equation = Eq(eval(equation.split('=')[0].strip()), eval(equation.split('=')[1].strip()))
    solution = solve(equation, x)
    if solution:
        if solution[0].is_integer:
            return int(solution[0])
        else:
            raise ValueError('Solution is not a whole number.')
    else:
        raise ValueError('No solution found.')

