from sympy import symbols, Eq, solve

def solve_for_x(equation):
    x = symbols('x')
    equation = equation.replace(' ', '')
    lhs, rhs = equation.split('=')
    eq = Eq(eval(lhs), eval(rhs))
    solution = solve(eq, x)
