    else:
        return None

import re

def solve_for_x(equation):
    equation = equation.replace(' ', '')
    match = re.match('([+-]?\\d*x)([+-]\\d+)=([+-]?\\d+)', equation)
    if not match:
        raise ValueError('Equation not in expected format')
    term_with_x, constant_on_left, constant_on_right = match.groups()
    if 'x' in term_with_x:
        if term_with_x == 'x':
            coefficient_of_x = 1
        elif term_with_x == '-x':
            coefficient_of_x = -1
        else:
            coefficient_of_x = int(term_with_x[:-1])
    else:
        raise ValueError('Term with x not found')
    constant_on_left = int(constant_on_left)
    constant_on_right = int(constant_on_right)
    x_value = (constant_on_right - constant_on_left) / coefficient_of_x
    return x_value

import re

def solve_for_x(equation):

    def eval_expr(expr):
        expr = re.sub('(\\d+)([*/])(\\d+)', lambda m: str(eval(m.group(0))), expr)
        return eval(expr)
    left_part, right_part = equation.split('=')
    right_value = eval_expr(right_part)
    x_terms = re.findall('([+-]?\\s*\\d*)\\s*x', left_part)
    coefficient = sum((int(term.strip().replace(' ', '') or '+1') for term in x_terms))
    remaining_left = re.sub('[+-]?\\s*\\d*\\s*x', '', left_part).strip()
    if remaining_left:
        remaining_left_value = eval_expr(remaining_left)
    else:
        remaining_left_value = 0
    x_value = (right_value - remaining_left_value) / coefficient
    return x_value

