def trump_detector(ts):
    import re
    ts = ts.lower()
    vowel_count = 0
    extra_vowel_count = 0
    repeated_vowel_pattern = '([aeiou])\\1+'
    matches = re.findall(repeated_vowel_pattern, ts)
    for char in ts:
        if char in 'aeiou':
            vowel_count += 1
    for match in matches:
        count = ts.count(match)
        extra_vowel_count += len(match) * count - count
    if vowel_count > 0:
        trump_index = round(extra_vowel_count / vowel_count, 2)
    else:
        trump_index = 0
    return trump_index

