def solve(p):
    if p == 2 or p == 5:
        return 1

    def is_n_sum_prime(n, p):
        power = pow(10, n, p)
        sum_mod = (power - 1) % p
        return sum_mod == 0

    def is_n_altsum_prime(n, p):
        power_pos = pow(10, n, p)
        power_neg = pow(-10, n, p)
        altsum_mod = (power_pos + power_neg - 2) % p
        return altsum_mod == 0
    for n in range(1, p + 1):
        if is_n_sum_prime(n, p) or is_n_altsum_prime(n, p):
            return n
    return p

