def solve(p):
    if not isprime(p):
        return 'Input must be a prime number.'

    def n_sum_checker(n, p):
        """Find the smallest n for sum-based divisibility rule."""
        k = 1
        while True:
            total = sum((int(str(p)[i:i + k]) for i in range(0, len(str(p)), k)))
            if total % p == 0:
                return k
            k += 1

    def n_altsum_checker(n, p):
        """Find the smallest n for alternating sum-based divisibility rule."""
        k = 1
        while True:
            alt_total = 0
            is_positive = True
            for i in range(0, len(str(p)), k):
                group = int(str(p)[i:i + k])
                alt_total += group if is_positive else -group
                is_positive = not is_positive
            if abs(alt_total) % p == 0:
                return k
            k += 1
    n = 1
    while True:
        sum_result = n_sum_checker(n, p)
        altsum_result = n - altsum_checker(n, p)
        if sum_result:
            return f"{p} is a '{sum_result}-sum' prime"
        elif altsum_result:
            return f"{p} is a '{altsum_result}-altsum' prime"
        n += 1

