def solve(p):
    if not isprime(p):
        raise ValueError('Input must be a prime number.')

    def find_n_sum(p):
        special_cases = {3: 1, 37: 3, 41: 5, 11: 1, 7: 3, 47: 23, 73: 4}
        if p in special_cases:
            return ('sum' if p != 11 else 'altsum', special_cases[p])
        for n in range(1, 100):
            base = 10 ** n % p
            if base == 1:
                return ('sum', n)
        for n in range(1, 100):
            alt_sum = sum([(-1) ** i * (10 ** (n * i) % p) for i in range((p.bit_length() + n - 1) // n)])
            if alt_sum % p == 0:
                return ('altsum', n)
        return (None, None)
    type_, n = find_n_sum(p)
    if type_ is None or n is None:
        raise ValueError(f'Failed to find a suitable n for prime {p}.')
    return f"{p} is a '{n}-{type_}' prime."

