    discriminant = b ** 2 - 4 * a * c
    if discriminant < 0:
        return None
    sqrt_discriminant = math.sqrt(discriminant)
    root1 = (-b + sqrt_discriminant) / (2 * a)
    root2 = (-b - sqrt_discriminant) / (2 * a)
    if discriminant == 0:
        return round(root1 + root1, 2)
    return round(root1 + root2, 2)

