def kontti(s):
    import re

    def transform_word(word):
        vowels = 'aeiouyAEIOUY'
        pattern = '([bcdfghjklmnpqrstvwxzBCDFGHJKLMNPQRSTVWXZ]*)([aeiouyAEIOUY].*)'
        match = re.match(pattern, word)
        if match:
            consonants, rest = match.groups()
            transformed = rest + consonants + 'kontti'
            return transformed
        else:
            return word + 'kontti'
    words = s.split()
    transformed_words = [transform_word(word) for word in words]
    return ' '.join(transformed_words)

def kontti(s):
    import re

    def transform_word(word):
        vowels = 'aeiouy'
        match = re.search(f'[^{vowels}]*[{' + vowels + '}]', word)
        if match:
            prefix = match.group()
            suffix = word[match.end():]
            transformed_prefix = prefix[::-1] + 'kontti'
            return transformed_prefix + suffix
        else:
            return word + 'kontti'
    words = s.split()
    transformed_words = [transform_word(word) for word in words]
    return ' '.join(transformed_words)

