from typing import List

def kontti(s: str) -> str:
    """Transforms a sentence into Kontti language.

    Args:
        s (str): The input sentence to transform.

    Returns:
        str: The transformed sentence in Kontti language.
    """
    vowels = 'aeiouyAEIOUY'

    def convert_word(word: str) -> str:
        """Converts a single word into Kontti language."""
        for i, letter in enumerate(word):
            if letter in vowels:
                if i > 0:
                    word = word[i] + word[:i] + word[i + 1:]
                break
        return word + '-kontti'
    words = s.split()
    converted_words = [convert_word(word) for word in words]
    return ' '.join(converted_words)

def kontti(s):
    import re

    def transform_word(word):
        vowels = 'aeiouyAEIOUY'
        first_vowel_index = next((i for i, c in enumerate(word) if c in vowels), None)
        if first_vowel_index is not None:
            consonants = word[:first_vowel_index]
            rest = word[first_vowel_index:]
            transformed_word = ''.join(list(consonants[::-1]) + [rest])
        else:
            transformed_word = word
        return transformed_word + '-kontti'
    words = re.findall('\\b\\w+\\b', s)
    non_words = re.split('\\b\\w+\\b', s)
    result = []
    word_iter = iter(words)
    for i, part in enumerate(non_words):
        result.append(part)
        try:
            result.append(transform_word(next(word_iter)))
        except StopIteration:
            break
    result.append(''.join(non_words[len(words):]))
    return ''.join(result)

