def sort_array(xs):
    import numpy as np
    if not xs:
        return []
    odds = sorted([x for x in xs if x % 2 != 0])
    evens = sorted([x for x in xs if x % 2 == 0], reverse=True)
    result = []
    odd_index, even_index = (0, 0)
    for x in xs:
        if x % 2 == 0:
            result.append(evens[even_index])
            even_index += 1
        else:
            result.append(odds[odd_index])
            odd_index += 1
    return result

