def build_square(blocks):
    block_counts = Counter(blocks)
    valid_rows = [[4], [3, 1], [1, 3], [2, 2], [2, 1, 1], [1, 2, 1], [1, 1, 2], [1, 1, 1, 1]]
    for _ in range(4):
        found_valid_row = False
        for row in valid_rows:
            temp_block_counts = block_counts.copy()
            can_form_row = True
            for block in row:
                if temp_block_counts[block] <= 0:
                    can_form_row = False
                    break
                temp_block_counts[block] -= 1
            if can_form_row:
                found_valid_row = True
                block_counts = temp_block_counts
                break
        if not found_valid_row:
            return False
    return True

