def can_build_square(blocks: List[int]) -> bool:
    block_counts = Counter(blocks)
    valid_rows = [{4: 1}, {3: 1, 1: 1}, {2: 2}, {2: 1, 1: 2}, {1: 4}]

    def can_form_row(block_counts):
        for row in valid_rows:
            if all((block_counts[block] >= count for block, count in row.items())):
                for block, count in row.items():
                    block_counts[block] -= count
                return True
        return False
    for _ in range(4):
        if not can_form_row(block_counts):
            return False
    return True

