    num_chars_between = len(s) - 2
    reduced_num = reduce_to_single_digits(num_chars_between)
    return f'{first_char}{reduced_num}{last_char}'

def same_encryption(s1, s2):
    """Check if two strings have the same encryption."""
    return encrypt_string(s1) == encrypt_string(s2)

