    number_couples = []
    for item in arr:
        num1, num2 = map(int, item.split(','))
        difference = abs(num1 - num2)
        number_couples.append(((num1, num2), difference))
    number_couples.sort(key=lambda x: x[1], reverse=True)
    if number_couples[0][1] == 0:
        return False
    return number_couples[0][0]

