def diff(arr: List[str]) -> Union[Tuple[int, int], bool]:
    if not arr:
        return False
    couples = []
    for item in arr:
        try:
            num1, num2 = map(int, item.split(','))
            couples.append((num1, num2))
        except ValueError:
            return False
    if not couples:
        return False
    max_diff = None
    result_couple = None
    for num1, num2 in couples:
        current_diff = abs(num1 - num2)
        if max_diff is None or current_diff > max_diff:
            max_diff = current_diff
            result_couple = (num1, num2)
    if max_diff == 0:
        return False
    return result_couple
